insert  into `application_article`(`id`,`title`,`content`,`num_id`,`type`,`objtype`) values (10,'申请须知','<p>&nbsp;</p><p><strong>一、办理部门</strong></p><p>申请人办理作品著作权和合同变更或补充登记申请，可来人到中国动漫作品版权服务平台各分中心办理，或通过邮寄方式向中国动漫作品版权服务平台动漫事业部提交登记申请材料办理。</p><p><br />中国动漫作品版权服务平台动漫事业部</p><p>地址：北京市东城区和平里南街5区18号龙绍衡大厦3层316室。<br />邮编：100013</p><p>电话：（010）84229644-8015</p><p>传真：（010）64004428</p><p><strong>&nbsp;</strong></p><p><strong>二、适用范围</strong></p><p>作品著作权和合同变更或补充登记申请适用于以下几个情形：</p><p>（1）申请人名称、地址变更；<br />（2）作品名称变更；<br />（3）申请登记材料填写错误。</p><p>上述情况办理变更登记。作品内容变化或增加时办理补充登记。</p><p>&nbsp;</p><p><strong>三、材料要求</strong></p><p>申请作品著作权和合同变更或补充登记应当提交的材料及要求：&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </p><p>（1）&nbsp;按要求填写的作品著作权变更或补充登记申请表；<br />（2）&nbsp;申请人的身份证明；<br />（3）&nbsp;变更或补充的作品样本；<br />（4）&nbsp;变更的证明材料；<br />（5）&nbsp;委托他人代为申请的，应提交申请人出具的授权书；<br />（6）&nbsp;代理人的身份证明。</p><p><strong>&nbsp;</strong></p><p><strong>四、办理时限</strong></p><p>中国动漫作品版权服务平台动漫事业部受理登记申请后，一般在30个工作日内办理完成。需要补正材料的，申请人自接到补正通知书后两个月内完成补正，中国动漫作品版权服务平台动漫事业部自收到符合要求的补正材料后30个工作日办理完成</p><p>&nbsp;</p><p><strong>五、缴费要求</strong><br />&nbsp;&nbsp;&nbsp;&nbsp;申请人办理作品著作权登记申请应按规定缴纳费用。<br />&nbsp;&nbsp;&nbsp;&nbsp;申请人收到缴费通知书后，应于10日内直接到中国动漫作品版权服务平台各分中心缴费，或通过银行或邮局汇付中国动漫作品版权服务平台动漫事业部。汇付登记费用的，应在汇单中写明作品名称、申请流水号。</p><p><br />帐户名称： <br />开户银行： <br />行号： <br />银行帐号： </p><p>&nbsp;</p><br />',1,'z61',1);
